define('text!chapter.template.html',[],function () { return '{{^appcache_loading}}\n<div class="row">\n  <div class="col-md-12">\n    {{#chapter.img}}\n    <div class="img">\n      <img src="{{url}}" />\n    </div>\n    {{/chapter.img}}\n\n\n    <div>\n    {{{content}}}\n    </div>\n    {{#next}}\n      <div>\n        <p class="clue">{{clue}}</p>\n\n\n          <div class="form-inline" role="form">\n\n            {{#unlocked}}\n              <div class="form-group">\n                  <input type="text" class="form-control" placeholder="UNLOCKED" disabled>\n              </div>\n              <a type="button" class="btn btn-default" href="#/{{unlocked}}">Next &raquo;</a>\n            {{/unlocked}}\n\n            {{^unlocked}}\n              <div class="form-group">\n                  <input type="text" class="form-control" value=\'{{.pass}}\' placeholder="Answer" on-enter=\'crack\'>\n              </div>\n              {{^unlocking}}\n              <button type="button" class="btn btn-default" on-tap="crack">Next &raquo;</button>\n              {{/unlocking}}\n              {{#unlocking}}\n                <span class="unlocking">Unlocking, please wait...</span>\n              {{/unlocking}}\n              {{#.error}}\n                <div class="error">{{.error}}</div>\n              {{/.error}}\n\n              <ul class="hints">\n              {{#all_hints}}\n                <li class="hint">{{.}}</li>\n              {{/all_hints}}\n              </ul>\n            {{/unlocked}}\n\n          </div>\n      </div>\n    {{/next}}\n  </div>\n</div>\n{{^next}}\n<div class="row">\n  <div class="col-md-12">\n\n\n\n        <div class="well">\n          <h3>Story End</h3>\n\n\n          {{^submitted}}\n            {{#network_available}}\n            <button class="btn btn-primary" on-tap="submit_time">Submit Time &raquo;</button>\n            {{/network_available}}\n            {{^network_available}}\n              <p>Connect to the internet to submit your results.</p>\n            {{/network_available}}\n\n            {{#time_err}}\n              <b>Failed to submit</b>\n            {{/time_err}}\n          {{/submitted}}\n\n          {{#submitted}}\n            {{#time.end}}\n              <p>\n                 Finished: {{time.end_pretty}}<br>\n                 Duration: {{time.pretty}} (hh:mm:ss)\n              </p>\n            {{/time.end}}\n            {{^time.user_id}}\n              <p>Anonymously submitted. Login to claim your time</p>\n              <a class="btn btn-primary" href="./{{chapter.id}}/login">Login &raquo;</a>\n            {{/time.user_id}}\n            {{#time.user_id}}\n              {{#chapter.end_link}}\n                  <button class="btn btn-primary" on-tap="end_link">Next &raquo;</button>\n              {{/chapter.end_link}}\n            {{/time.user_id}}\n          {{/submitted}}\n        </div>\n\n  </div>\n</div>\n{{/next}}\n{{/appcache_loading}}\n';});

